    r = re.findall('(?s)f1-podium--item">\s*<a href="([^"]*).*?ize">([^<]*)', u)
    sort = [(i[0],i[1].replace('\xc2\xa0','').replace('&','').replace('#','').replace('8211','').replace(';','')) for i in r]
    return sort

